mapper.select:
SELECT b.id sessionId, a.member_id memberId, a.member_username memberUsername, c.id companyId, c.chinesename, c.contactperson, c.phone, d.chinese_name companyNature, c.addtime, c.useable, c.url, c.audit_status auditStatus, c.audittime audittime FROM web_member a INNER JOIN web_member_session b on a.member_id = b.member_id and b.id = #{session} INNER JOIN ebs_companyinfo c on b.orgnization_id = c.id LEFT JOIN sys_company_type d on c.exhibitors_nature_id = d.id and d.use_flag = 'Y' WHERE a.member_type = #{leixing}
and a.member_status = 0
and c.chinesename like concat ('%',#{companyName},'%')
and a.member_username like concat ('%',#{memberUsername},'%')
and c.addtime >= #{startTime}
and c.addtime <= #{endTime}
and c.useable = #{useable}
and c.audit_status = 1
and c.audit_status = 2
and c.audit_status = 3
and c.phone = #{phone}
order by c.addtime desc limit #{index}, #{size}

		SELECT count(*) FROM web_member a
		INNER JOIN web_member_session b on a.member_id = b.member_id and b.id = #{session}
		INNER JOIN ebs_companyinfo c on b.orgnization_id = c.id
		LEFT JOIN sys_company_type d on c.exhibitors_nature_id = d.id and d.use_flag = 'Y'
		WHERE a.member_type = #{leixing}

            and a.member_status = 0


            and c.chinesename like concat ('%',#{companyName},'%')


            and a.member_username like concat ('%',#{memberUsername},'%')


            and c.addtime >= #{startTime}


            and c.addtime <= #{endTime}


			and c.useable = #{useable}


			and c.audit_status = 1


			and c.audit_status = 2


			and c.audit_status = 3


			and c.phone = #{phone}

mapper.update:
update ebs_companyinfo
id = #{id}, chinesename = #{chinesename}, engname = #{engname}, russianname = #{russianname}, chineseaddress = #{chineseaddress}, engaddress = #{engaddress}, postcode = #{postcode}, phone = #{phone}, tel = #{tel}, fax = #{fax}, website = #{website}, email = #{email}, country = #{country}, province = #{province}, city = #{city}, contactperson = #{contactperson}, jobtitle = #{jobtitle}, principal = #{principal}, industryid = #{industryid}, busscope = #{busscope}, hopecustomers = #{hopecustomers}, companyprofile = #{companyprofile}, buslicensepath = #{buslicensepath}, relateddocumentspath = #{relateddocumentspath}, purchaseintention = #{purchaseintention}, exhibitors_nature_id = #{exhibitorsNatureId}, `companypicture` = #{companypicture}, `companyvideo` = #{companyvideo}, `companypictures` = #{companypictures}, `companylogo` = #{companylogo}, `organizationcode` = #{organizationcode}, `url` = #{url}, useable = #{useable}, audit_status = #{auditStatus}, audit_remark = #{auditRemark}, tradinggroupid = #{tradinggroupid}, audittime = null,auditername = null, `audittime` = #{audittime}, `auditername` = #{auditername},
where id = #{id}

mapper.select:
SELECT a.id companyId, a.comany_type_id, c.member_username memberUsername, a.chinesename, a.engname, a.russianname, a.chineseaddress, a.engaddress, a.postcode, a.phone, a.tel, a.fax, a.website, a.email, ifnull(a.country,0) as country, ifnull(a.province,0) as province, ifnull(a.city,0) as city, a.contactperson, a.jobtitle, a.principal, a.industryid, a.busscope, a.hopecustomers, a.companyprofile, a.companylogo, ifnull(a.buslicensepath, d.apply_license) buslicensepath, d.apply_file relateddocumentspath, a.purchaseintention, a.exhibitors_nature_id, date(a.addtime) addtime, a.companypicture, a.companyvideo, a.companypictures, a.relateddocumentspath, a.organizationcode, a.url, a.tradinggroupid, a.legalpersonname, a.legalpersoncardnumber, a.legalpersoncardpath, a.admissionapplicationform, a.workshopcertificate, a.technicalproofdocuments, a.othersupportingdocuments, a.previouscase, a.audittime, a.audit_status as auditStatus, a.audit_remark as auditRemark FROM ebs_companyinfo a LEFT JOIN web_member_session b ON a.id = b.orgnization_id LEFT JOIN web_member c ON b.member_id = c.member_id LEFT JOIN ebs_booth_apply d ON d.company_id = a.id and d.session_id = #{session}
b.id = #{session}
and a.id = #{id}
and a.phone = #{phone}
and a.email = #{email}
order by b.addtime desc limit 1

SELECT s.sessionid as sessionname,
b.member_id memberId, b.member_username memberUsername,
c.id companyId, c.chinesename, c.contactperson, c.phone, d.chinese_name companyNature, c.addtime, c.useable,c.url FROM ebs_companyinfo c inner join sys_session as s on s.id=c.session
left join ( select a.member_username,a.member_id,b.id,b.orgnization_id from web_member as a inner join web_member_session as b on a.member_id=b.member_id where a.member_type=2 ) as b on c.id=b.orgnization_id and c.session=b.id
LEFT JOIN sys_company_type d on c.exhibitors_nature_id = d.id and d.use_flag = 'Y' WHERE c.comany_type_id = 2 and c.session != #{nowsession}
and c.session = #{session}
and b.id = #{session}
and c.chinesename like concat ('%',#{companyName},'%')
and b.member_username like concat ('%',#{memberUsername},'%')
and c.addtime >= #{startTime}
and c.addtime <= #{endTime}
and c.useable = #{useable}
order by c.addtime desc limit #{index}, #{size}

		SELECT
        	count(*)
        FROM ebs_companyinfo c       
		inner join sys_session as s on s.id=c.session	

		left join (
			select a.member_username,a.member_id,b.id,b.orgnization_id from web_member as a
			inner join web_member_session as b on a.member_id=b.member_id
			where a.member_type=2
		) as b on c.id=b.orgnization_id and c.session=b.id

		WHERE c.comany_type_id = 2
		 and c.session != #{nowsession}  

			and c.session = #{session}


            and b.id = #{session} 


            and c.chinesename like concat ('%',#{companyName},'%')


            and b.member_username like concat ('%',#{memberUsername},'%')


            and c.addtime >= #{startTime}


            and c.addtime <= #{endTime}


            and c.useable = #{useable}
