mapper.select:
        select a.id,a.session,a.companyname,a.companyid
        ,a.remark,a.agent,vw.uname as agentname,a.status,a.addtime
        ,a.printstatus
        ,case printstatus
        when 0 then '未打印'
        when 1 then '打印中'
        when 2 then '已打印'
        end as printstatusname
        ,a.printtype
        ,case printtype when -1 then ''
        when 0 then '正常打印'
        when 1 then 'IC卡打印' end printtypename,a.printtime
        ,a.makecardtime,
        a.isforensics as isforensics,
        a.forensicstime as forensicstime,
        a.certificatenumber as certificatenumber
        ,a.printnumber as printnumber
        ,a.reviewremark
        ,a.isplastic
        ,a.isback
        ,a.plastictime
		,a.relationcompanyid
		,a.relationcompanyname
		,a.paystatus
		,case a.paystatus when 0 then '待缴费' when 1 then '已缴费' end paystatusname
		,a.audittime
        ,a.safetyresponsibilitycommit
        ,a.fitmentapplication
        ,a.effectdiagram
        ,a.constructiondiagram
        ,a.pointdiagram
        ,a.circuitdiagram
        ,a.workercertificatecopy
        ,a.equipmentqualifiedprove
        ,a.workercasualtyprove
        from ebs_stadium as a
        left join vw_Frontend_user_integration as vw on vw.isback=a.isback and vw.uid=a.agent


                and a.id = #{id}


                and a.session = #{session}


                and a.companyname like concat('%',#{companyname},'%')


                and a.relationcompanyid = #{relationcompanyid}


				and a.relationcompanyname like concat('%',#{relationcompanyname},'%')


				and a.companyid = #{companyid}


                and a.remark = #{remark}


                and a.agent = #{agent}


                and a.status = #{status}

            and a.isback = #{isback}
            and a.printstatus = #{printstatus}
            and a.printtype = #{printtype}
            and a.cardnature = #{cardnature}
            and a.makecardtime = #{makecardtime}

                and a.addtime = #{addtime}


                and a.isforensics = #{isforensics}


                and a.forensicstime = #{forensicstime}


                and a.certificatenumber = #{certificatenumber}


                and a.printnumber = #{printnumber}


                and a.isplastic = #{isplastic}


                and a.paystatus = #{paystatus}


                and a.audittime = #{audittime}

select a.id,a.session,companyname,a.companyid ,a.remark,a.agent,vw.uname as agentname ,a.status,a.addtime,vw.uname ,a.printstatus ,case printstatus when 0 then '未打印' when 1 then '打印中' when 2 then '已打印' end as printstatusname ,a.printtype ,case printtype when -1 then '' when 0 then '正常打印' when 1 then 'IC卡打印' end printtypename,a.printtime ,a.makecardtime, a.isforensics as isforensics, a.forensicstime as forensicstime, a.certificatenumber as certificatenumber, a.printnumber as printnumber,a.reviewremark ,a.isplastic ,ifnull(s.middleid,'') as middleid ,a.isback ,a.plastictime ,a.relationcompanyid ,a.relationcompanyname ,a.paystatus ,case a.paystatus when 0 then '待缴费' when 1 then '已缴费' end paystatusname ,a.updatetime ,a.audittime ,a.safetyresponsibilitycommit ,a.fitmentapplication ,a.effectdiagram ,a.constructiondiagram ,a.pointdiagram ,a.circuitdiagram ,a.workercertificatecopy ,a.equipmentqualifiedprove ,a.workercasualtyprove from ebs_stadium as a
left join vw_agentstatus as m on m.memberid=a.agent
left join vw_Frontend_user_integration as vw on vw.isback=a.isback and vw.uid=a.agent left join ebs_companyinfo as d on d.chinesename=a.companyname and a.session=d.session left join web_member_session as s on s.member_id=a.agent and s.id=#{session}
and ifnull(m.makeevidence,1)=1
and a.id = #{id}
and a.session = #{session}
and a.companyname like concat('%',#{companyname},'%')
and a.companyid = #{companyid}
and a.relationcompanyname like concat('%',#{relationcompanyname},'%')
and a.relationcompanyid = #{relationcompanyid}
and a.remark = #{remark}
and a.agent = #{agent}
and a.status = #{status} and a.isback = #{isback} and a.printstatus = #{printstatus} and a.printtype = #{printtype} and a.makecardtime = #{makecardtime} and a.addtime = #{addtime}
and a.isforensics = #{isforensics}
and a.forensicstime = #{forensicstime}
and a.certificatenumber = #{certificatenumber}
and a.printnumber = #{printnumber}
and a.isplastic = #{isplastic}
and a.paystatus = #{paystatus}
and a.audittime = #{audittime}
order by a.id desc limit #{index}, #{size}

SELECT count(*)
FROM
ebs_stadium as a

    left join vw_agentstatus as m on m.memberid=a.agent



        and ifnull(m.makeevidence,1)=1

    and a.id =
        #{id}

    and a.session =
        #{session}


        and a.companyname like concat('%',#{companyname},'%')


        and a.companyid = #{companyid}


        and a.relationcompanyname like concat('%',#{relationcompanyname},'%')


        and a.relationcompanyid = #{relationcompanyid}

    and a.remark =
        #{remark}

    and a.agent =
        #{agent}

    and a.status = #{status}
    and a.isback = #{isback}
    and a.printstatus = #{printstatus}
    and a.printtype = #{printtype}
    and a.makecardtime = #{makecardtime}
    and a.addtime =
        #{addtime}


        and a.isforensics = #{isforensics}


        and a.forensicstime = #{forensicstime}


        and a.certificatenumber = #{certificatenumber}


        and a.printnumber = #{printnumber}


        and a.isplastic = #{isplastic}


        and a.paystatus = #{paystatus}


        and a.audittime = #{audittime}

